import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import Text from './Input/UserInput';
import Components from './Output/UserOutput';


class App extends Component {
  state = {
    name: [
      {firstUser: 'BR'}
    ],
    other: 'SomeValue'
  }

  nameChangeHandler = (updatedName) => {
    this.setState({
      name: [
        {firstUser: updatedName}
      ]
    });
  }

  render() {

    return (
      <div className="App">
        <h1>First React Assignment</h1>
        <Components
          username='Bishal R'
          newName={this.state.name[0].firstUser}>
        </Components>
      </div>
    );
  }
}

export default App;
