import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Components from './Person/Person';


class App extends Component {
  state = {
    persons:
    [
      {name: 'Bishal', age: '25'},
      {name: 'Raktim', age: '26'},
      {name: 'Yatra', age: '27'}
    ],
    otherState: 'SomeOtherValue'
  }


  switchNameHandler = () => {
    //alert('The button is clicked!');
    this.setState({
      persons:
      [
        {name: 'Bishal Raktim', age: '29'},
        {name: 'Paila', age: '30'},
        {name: 'Sangram', age: '31'}
      ]
    })
  }

  render() {
    return (
      <div className="App">
      <h1>Learning React from Scratch!</h1>
      <button onClick={this.switchNameHandler}>Switch Name </button>
      <Components name={this.state.persons[0].name} age={this.state.persons[0].age} />

      <Components name={this.state.persons[1].name} age={this.state.persons[1].age}>
        My Hobbies: Playing Chess
      </Components>

      <Components name={this.state.persons[2].name} age={this.state.persons[2].age} />
      </div>
    );
  }
}



export default App;
