import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import Components from './Output/UserOutput';


class App extends Component {
  state = {
    name: [
      {firstUser: 'BR'}
    ],
    other: 'SomeValue'
  }

  nameChangeHandler = (updatedName) => {
    this.setState({
      name: [
        {firstUser: updatedName}
      ]
    });
  }

  eventHandler = (e) => {
    this.setState({
      name: [
        {firstUser: e.target.value}
      ]
    });
  }

  render() {

    const style = {
      backgroundColor: 'green',
      width: '28%',
      margin: '10px auto -3px'
    }

    return (
      <div className="App">
        <h1 style={style}>First React Assignment</h1>
        <Components
          username='Bishal R'
          newName={this.state.name[0].firstUser}
          click={this.nameChangeHandler.bind(this, 'Daniel Hamilton!')}
          change={this.eventHandler}>
        </Components>
      </div>
    );
  }
}

export default App;
